<?phpuse App\Client;use App\Billet;use App\Compagnie;use App\Reservation;use App\Vol;/*|--------------------------------------------------------------------------| Web Routes|--------------------------------------------------------------------------|| Here is where you can register web routes for your application. These| routes are loaded by the RouteServiceProvider within a group which| contains the "web" middleware group. Now create something great!|*///----------------------RESOURCE ET CONTROLLEUR ADMIN-----------------------//Route::Resource('reservers','receptionReservationController');Route::Resource('client','receptionClientController');Route::resource('compagnies','CompagnieController');Route::resource('billets','BilletController');Route::resource('vols','VolController');Route::resource('reservations','ReservationController');Route::resource('clients','ClientController');Route::get('/', function () {    return view('user.home');})->name('home_path');Route::get('accueil', function () {    return view('user.home');})->name('accueil_path');Route::get('reservation', function (){	$vols=DB::table('vol')            ->join('billet','vol.IdVol','=','billet.IdVol')            ->join('compagnie','vol.IdCompagnie','=','compagnie.IdCompagnie')            ->select('vol.*','billet.prix','compagnie.logo')            ->get();    return view('user.reservation',compact('vols'));    })->name('reservation_path');Route::post('reservation',function(){	return view('user.register');})->name('reservation_post');Route::get('about', function () {    return view('user.about');})->name('about_path');Route::get('register', function () {    return view('user.register');})->name('register_path');Route::get('contact', function () {    return view('user.contact');})->name('contact_path');Route::get('admin',function(){	return view('admin.index');})->name('admin_path');// --------------------------Routes pour le receptionniste ------------------//Route::get('reception',function(){    return view('receptionniste.index');})->name('reception_path');Route::get('reception/clients',function(){    $clients=Client::all();    return view('receptionniste.client.index',compact('clients'));})->name('reception_client');Route::get('reception/billets',function(){     $billets=DB::table('billet')            ->join('vol','billet.IdVol','=','vol.IdVol')            ->join('compagnie','vol.IdCompagnie','=','compagnie.IdCompagnie')            ->select('billet.*','vol.CodeVol','vol.NbrePlace','compagnie.NomCompagnie')            ->get();        return view('receptionniste.billet.index',compact('billets'));})->name('reception_billet');Route::get('reception/vols',function(){    $vols=DB::table('vol')            ->join('compagnie','vol.IdCompagnie','=','compagnie.IdCompagnie')            ->select('vol.*','compagnie.NomCompagnie')            ->get();        return view('receptionniste.vol.index',compact('vols'));})->name('reception_vol');Route::get('reception/compagnies',function(){    $compagnies= Compagnie::all();        return view('receptionniste.compagnie.index',compact('compagnies'));})->name('reception_compagnie');Route::get('reception/reservations',function(){    $reservations=DB::table('reservation')            ->join('client','reservation.IdClient','=','client.IdClient')            ->join('vol','reservation.IdVol','=','vol.IdVol')            ->select('reservation.*','vol.*','client.*')            ->get();    return view('receptionniste.reservation.index',compact('reservations'));})->name('reception_reservation');